'use client';

import { useQuery } from '@tanstack/react-query';
import { useState } from 'react';
import Link from 'next/link';

const fetchRandom = async () => {
    // Artificial delay to make loading state visible
    await new Promise(r => setTimeout(r, 1000));
    return Math.random().toString(36).substring(7).toUpperCase();
};

function CachedData() {
    const { data, isLoading, isStale, isFetching, dataUpdatedAt } = useQuery({
        queryKey: ['caching-demo'],
        queryFn: fetchRandom,
        staleTime: 5000, // Considered fresh for 5 seconds
        gcTime: 10000,   // Kept in memory for 10 seconds
    });

    // eslint-disable-next-line react-hooks/purity
    const timeSinceUpdate = dataUpdatedAt ? Date.now() - dataUpdatedAt : 0;

    return (
        <div className="space-y-4">
            <div className="p-6 bg-white rounded-xl border border-slate-200 shadow-sm">
                <div className="flex justify-between items-start mb-4">
                    <div>
                        <div className="text-sm font-bold text-slate-400 uppercase tracking-wider">Data ID</div>
                        {isLoading ? (
                            <div className="h-8 w-24 bg-slate-200 animate-pulse rounded mt-1" />
                        ) : (
                            <div className="text-3xl font-mono font-bold text-slate-900">{data}</div>
                        )}
                    </div>
                    <div className="text-right">
                        <div className="inline-flex items-center gap-1.5 px-2.5 py-0.5 rounded-full text-xs font-medium bg-slate-100 text-slate-600">
                            {isLoading ? 'Loading' : isFetching ? 'Background Refetch' : isStale ? 'Stale (Cached)' : 'Fresh (Cached)'}
                        </div>
                    </div>
                </div>

                <div className="text-xs text-slate-500 font-mono">
                    Last Updated: {dataUpdatedAt ? new Date(dataUpdatedAt).toLocaleTimeString() : 'Never'}
                </div>
            </div>

            <div className="text-sm text-slate-600 bg-blue-50 p-4 rounded-lg border border-blue-100">
                <strong>Experiment:</strong>
                <ul className="list-disc pl-5 space-y-1 mt-2">
                    <li>Click &quot;Back to Features&quot; to unmount this component.</li>
                    <li>Return within <strong>5s</strong>: Data shows instantly (Fresh).</li>
                    <li>Return between <strong>5-10s</strong>: Data shows instantly (Stale) then updates.</li>
                    <li>Return after <strong>10s</strong>: Hard loading state (Cache Garbage Collected).</li>
                </ul>
            </div>
        </div>
    );
}

export default function CachingPage() {
    const [showDemo, setShowDemo] = useState(true);

    return (
        <div className="space-y-8">
            <div>
                <h1 className="text-3xl font-bold text-slate-900">Caching Mechanics</h1>
                <p className="text-slate-600 mt-2">
                    Understanding `staleTime` and `gcTime` (Garbage Collection Time).
                </p>
            </div>

            <div className="glass-panel p-8 rounded-2xl min-h-[400px]">
                {showDemo ? (
                    <div className="space-y-8">
                        <CachedData />
                        <button
                            onClick={() => setShowDemo(false)}
                            className="w-full py-3 border-2 border-slate-200 rounded-xl text-slate-600 font-bold hover:bg-slate-50 hover:border-slate-300 transition-colors"
                        >
                            Unmount Component (Simulate Navigation Away)
                        </button>
                    </div>
                ) : (
                    <div className="flex flex-col items-center justify-center h-full py-12 text-center space-y-6">
                        <div className="text-slate-400">
                            Component Unmounted. Cache timer is ticking...
                        </div>
                        <button
                            onClick={() => setShowDemo(true)}
                            className="px-8 py-3 bg-indigo-600 text-white font-bold rounded-xl shadow-lg hover:bg-indigo-700 transition-transform active:scale-95"
                        >
                            Mount Component (Simulate Navigation Back)
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
}

'use client';

import Link from 'next/link';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { useState, type FormEvent } from 'react';
import TechnicalExplainer from '@/app/components/TechnicalExplainer';

// Mock API
type Todo = {
    id: string;
    text: string;
    completed: boolean;
};

// Initial data
const initialTodos: Todo[] = [
    { id: '1', text: 'Learn React Query', completed: true },
    { id: '2', text: 'Implement Mutations', completed: false },
];

// Shared in-memory "database" roughly simulated
let dbTodos = [...initialTodos];

// Delay for effect
const delay = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));

const fetchTodos = async (): Promise<Todo[]> => {
    await delay(800);
    return [...dbTodos];
};

const addTodo = async (text: string): Promise<Todo> => {
    await delay(800);
    const newTodo = { id: Math.random().toString(36).substring(7), text, completed: false };
    dbTodos = [...dbTodos, newTodo];
    return newTodo;
};

export default function MutationsPage() {
    const [text, setText] = useState('');
    const queryClient = useQueryClient();

    const { data: todos, isLoading } = useQuery({
        queryKey: ['todos'],
        queryFn: fetchTodos,
    });

    const mutation = useMutation({
        mutationFn: addTodo,
        onMutate: async (newText) => {
            // Cancel any outgoing refetches (so they don't overwrite our optimistic update)
            await queryClient.cancelQueries({ queryKey: ['todos'] });

            // Snapshot the previous value
            const previousTodos = queryClient.getQueryData<Todo[]>(['todos']);

            // Optimistically update to the new value
            queryClient.setQueryData<Todo[]>(['todos'], (old) => {
                return old ? [...old, { id: 'temp-' + Date.now(), text: newText, completed: false }] : [];
            });

            // Return a context object with the snapshotted value
            return { previousTodos };
        },
        onError: (err, newTodo, context) => {
            // If the mutation fails, use the context returned from onMutate to roll back
            if (context?.previousTodos) {
                queryClient.setQueryData(['todos'], context.previousTodos);
            }
        },
        onSettled: () => {
            // Always refetch after error or success:
            queryClient.invalidateQueries({ queryKey: ['todos'] });
            setText('');
        },
    });

    const handleSubmit = (e: FormEvent) => {
        e.preventDefault();
        if (text.trim()) {
            mutation.mutate(text);
        }
    };

    return (
        <div className="max-w-4xl mx-auto p-6 space-y-8">
            <div className="flex items-center gap-4">
                <Link
                    href="/features/query"
                    className="px-4 py-2 bg-slate-100 hover:bg-slate-200 rounded-lg text-slate-600 transition-colors"
                >
                    ΓåÉ Back
                </Link>
                <div>
                    <h1 className="text-3xl font-bold text-slate-900">Mutations & Optimistic UI</h1>
                    <p className="text-slate-600">Handling data updates with automatic retry, rollbacks, and optimistic updates.</p>
                </div>
            </div>

            <div className="grid md:grid-cols-2 gap-12 items-start">
                <div className="bg-white p-8 rounded-2xl border border-slate-200 shadow-lg space-y-6">
                    <form onSubmit={handleSubmit} className="flex gap-2">
                        <input
                            type="text"
                            value={text}
                            onChange={(e) => setText(e.target.value)}
                            placeholder="Add a new todo..."
                            disabled={mutation.isPending}
                            className="flex-1 px-4 py-3 rounded-lg border border-slate-300 focus:outline-none focus:ring-4 focus:ring-emerald-100 focus:border-emerald-300 transition-all"
                        />
                        <button
                            type="submit"
                            disabled={!text.trim() || mutation.isPending}
                            className="px-6 py-3 bg-emerald-600 text-white rounded-lg font-medium hover:bg-emerald-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all active:scale-95"
                        >
                            Add
                        </button>
                    </form>

                    <div className="space-y-4">
                        <h3 className="font-semibold text-slate-800">Todo List</h3>
                        {isLoading ? (
                            <div className="space-y-3 animate-pulse">
                                {[1, 2, 3].map(i => (
                                    <div key={i} className="h-12 bg-slate-100 rounded-lg"></div>
                                ))}
                            </div>
                        ) : (
                            <ul className="space-y-3">
                                {todos?.map((todo) => (
                                    <li
                                        key={todo.id}
                                        className={`flex items-center p-3 rounded-lg border ${todo.id.startsWith('temp-')
                                            ? 'bg-emerald-50 border-emerald-200 opacity-70'
                                            : 'bg-slate-50 border-slate-200'
                                            } transition-all`}
                                    >
                                        <div className={`w-5 h-5 rounded-full border-2 mr-3 flex items-center justify-center ${todo.completed ? 'bg-emerald-500 border-emerald-500' : 'border-slate-300'
                                            }`}>
                                            {todo.completed && <span className="text-white text-xs">Γ£ô</span>}
                                        </div>
                                        <span className={todo.completed ? 'text-slate-400 line-through' : 'text-slate-700'}>
                                            {todo.text}
                                        </span>
                                        {todo.id.startsWith('temp-') && (
                                            <span className="ml-auto text-xs text-emerald-600 font-medium animate-pulse">
                                                Saving...
                                            </span>
                                        )}
                                    </li>
                                ))}
                                {todos?.length === 0 && (
                                    <li className="text-slate-400 text-center py-8">No todos yet. Add one!</li>
                                )}
                            </ul>
                        )}
                    </div>
                </div>

                <div className="space-y-8">
                    <div className="bg-emerald-900 text-emerald-50 p-6 rounded-xl shadow-inner">
                        <h3 className="font-bold text-lg mb-4 text-white">How it works (Simple)</h3>
                        <p className="text-sm text-emerald-100 mb-4">
                            Imagine sending a text message. You hit "Send" and it appears in the chat bubble <strong>instantly</strong> (Optimistic Update).
                        </p>
                        <p className="text-sm text-emerald-100">
                            Your phone doesn't wait for the cell tower to say "Okay, delivered" before showing you the message. It assumes it will work. If it fails (no signal), it shows a red "Retry" icon (Rollback).
                        </p>
                    </div>

                    <TechnicalExplainer
                        title="Optimistic UI Updates"
                        analogy="It&apos;s like writing on a piece of paper before the teacher sees it. You act like it&apos;s done immediately. If the teacher says &apos;Wait, that&apos;s wrong&apos;, you erase it."
                        points={[
                            "Optimistic Update: We force the screen to change immediately so it feels super fast.",
                            "Snapshot: We take a photo of the data BEFORE we change it, just in case we need to undo.",
                            "Rollback: If the server says 'Error!', we put the old data back using the snapshot."
                        ]}
                        codeSnippet={`// 1. Snapshot previous todos
const prev = queryClient.getQueryData(['todos']);

// 2. Optimistically add new todo
queryClient.setQueryData(['todos'], old => [...old, newTodo]);

// 3. If it fails, put 'prev' back!
onError: (err, newTodo, context) => {
  queryClient.setQueryData(['todos'], context.prev);
}`}
                    />
                </div>
            </div>
        </div>
    );
}

'use client';

import { useQuery, useQueryErrorResetBoundary } from '@tanstack/react-query';
import Link from 'next/link';
import { useState, useEffect } from 'react';
import TechnicalExplainer from '@/app/components/TechnicalExplainer';

// deterministically fail X times then succeed
let failCount = 0;
const MAX_FAILURES = 3;

const fetchFlaky = async () => {
    // Artificial delay
    await new Promise(r => setTimeout(r, 1000));

    // Simulate failure
    if (failCount < MAX_FAILURES) {
        failCount++;
        throw new Error('Server overloaded (Simulated Error)');
    }

    return {
        id: Math.random().toString(36).substr(2, 9),
        message: 'Success! You persisted.',
        timestamp: new Date().toLocaleTimeString()
    };
};

export default function RetriesPage() {
    const { reset } = useQueryErrorResetBoundary();
    const [key, setKey] = useState(0); // To reset the demo completely

    const { data, isLoading, isError, error, failureCount, failureReason, isSuccess, refetch } = useQuery({
        queryKey: ['retries-demo', key],
        queryFn: fetchFlaky,
        retry: 5, // retry 5 times
        retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 10000), // Exponential backoff: 1s, 2s, 4s...
    });

    const resetDemo = () => {
        failCount = 0;
        setKey(k => k + 1);
        reset(); // clear error boundaries if any
    };

    return (
        <div className="space-y-8">
            <div className="flex items-center gap-4">
                <Link
                    href="/features/query"
                    className="px-4 py-2 bg-slate-100 hover:bg-slate-200 rounded-lg text-slate-600 transition-colors"
                >
                    ΓåÉ Back
                </Link>
                <div>
                    <h1 className="text-3xl font-bold text-slate-900">Automatic Retries</h1>
                    <p className="text-slate-600">Smart error handling with exponential backoff strategies.</p>
                </div>
            </div>

            <div className="grid md:grid-cols-2 gap-8">
                <div className="space-y-6">
                    <div className="bg-white p-8 rounded-2xl border border-slate-200 shadow-lg relative overflow-hidden">

                        <div className="flex justify-between items-center mb-8">
                            <h2 className="text-xl font-bold text-slate-800">Status Monitor</h2>
                            <button
                                onClick={resetDemo}
                                className="text-sm font-medium text-indigo-600 hover:text-indigo-700 underline"
                            >
                                Reset Demo
                            </button>
                        </div>

                        <div className="flex flex-col gap-4">
                            {/* Visual Timeline */}
                            <div className="flex justify-between items-center text-xs font-semibold text-slate-400 mb-2 px-2">
                                <span>Start</span>
                                <span>Attempt 1</span>
                                <span>Attempt 2</span>
                                <span>Attempt 3</span>
                                <span>Success</span>
                            </div>
                            <div className="w-full h-3 bg-slate-100 rounded-full overflow-hidden flex">
                                <div className={`h-full transition-all duration-500 ease-out ${failureCount >= 0 && isLoading ? 'bg-amber-400' : isError ? 'bg-red-500' : 'bg-slate-200'}`} style={{ width: '20%' }} />
                                <div className={`h-full transition-all duration-500 ease-out border-l border-white ${failureCount >= 1 ? 'bg-amber-400' : 'bg-transparent'}`} style={{ width: '20%' }} />
                                <div className={`h-full transition-all duration-500 ease-out border-l border-white ${failureCount >= 2 ? 'bg-amber-400' : 'bg-transparent'}`} style={{ width: '20%' }} />
                                <div className={`h-full transition-all duration-500 ease-out border-l border-white ${failureCount >= 3 ? 'bg-emerald-500' : 'bg-transparent'}`} style={{ width: '40%' }} />
                            </div>

                            {isLoading && (
                                <div className="mt-4 p-4 bg-amber-50 border border-amber-200 rounded-xl flex items-center gap-3">
                                    <div className="w-5 h-5 border-2 border-amber-600 border-t-transparent rounded-full animate-spin"></div>
                                    <div>
                                        <div className="font-bold text-amber-900">Attempting to connect...</div>
                                        <div className="text-xs text-amber-700">Failure Count: {failureCount}</div>
                                        <div className="text-xs text-amber-600 font-mono mt-1">
                                            {failureReason ? `Last Error: ${failureReason.message}` : 'Connecting...'}
                                        </div>
                                    </div>
                                </div>
                            )}

                            {isSuccess && (
                                <div className="mt-4 p-4 bg-emerald-50 border border-emerald-200 rounded-xl flex items-center gap-3 animate-in fade-in slide-in-from-bottom-2">
                                    <div className="w-8 h-8 bg-emerald-100 rounded-full flex items-center justify-center text-emerald-600 font-bold">Γ£ô</div>
                                    <div>
                                        <div className="font-bold text-emerald-900">Connection Established</div>
                                        <div className="text-xs text-emerald-700">{data.message}</div>
                                    </div>
                                </div>
                            )}

                            {isError && (
                                <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-xl">
                                    <div className="font-bold text-red-900">Fatal Error</div>
                                    <div className="text-xs text-red-700">Max retries exceeded. The server is unreachable.</div>
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                <div className="space-y-6">
                    <div className="bg-slate-900 p-6 rounded-xl shadow-inner text-slate-300 transform rotate-1 hover:rotate-0 transition-transform duration-500">
                        <pre className="font-mono text-xs overflow-x-auto">
                            {`useQuery({
  queryKey: ['flaky-api'],
  queryFn: fetchFlaky,
  // Retry 3 times (default is 3)
  retry: 3, 
  
  // Exponential Backoff
  retryDelay: (attempt) => 
    Math.min(1000 * 2 ** attempt, 30000),
})`}
                        </pre>
                    </div>
                </div>
            </div>

            <TechnicalExplainer
                title="Retry Strategies"
                analogy="Knocking on a friend&apos;s door. You knock once. No answer. You wait 5 seconds. Knock again loudly. Wait 10 seconds. Knock really loud. If still no answer, you leave. You don&apos;t just rapid-fire knock 100 times in 1 second!"
                points={[
                    "Default Behavior: TanStack Query automatically retries failed queries 3 times before showing an error.",
                    "Exponential Backoff: It waits longer between each attempt (1s, 2s, 4s...) to avoid overwhelming a struggling server.",
                    "Configuration: You can customize `retry`, `retryDelay`, or turn it off entirely for 404s."
                ]}
                codeSnippet=""
            />
        </div>
    );
}

