import { revalidateTimePath } from '../actions';
import TechnicalExplainer from '@/app/components/TechnicalExplainer';

async function getTime() {
    // Fetch data, cache it for 1 hour by default if not specified, 
    // but here we want to show revalidation so we won't disable cache entirely
    // Next.js fetches are cached by default.
    const res = await fetch('http://localhost:3000/api/demo/time', {
        next: { tags: ['time-data'] }
    });

    // Handle case where fetch fails during build (optional but good practice)
    if (!res.ok) {
        return { formatted: 'Error fetching time' };
    }

    return res.json();
}

export default async function CacheRevalidatePage() {
    const data = await getTime();

    return (
        <div className="space-y-8">
            <div className="mb-8">
                <h1 className="text-3xl font-bold text-slate-900">Caching & Revalidation</h1>
                <p className="text-slate-600 mt-2">
                    Demonstrating Next.js Data Cache and Server Actions for revalidation.
                </p>
            </div>

            <div className="grid md:grid-cols-2 gap-8">
                {/* Server Time Card */}
                <div className="p-8 rounded-2xl bg-white border border-slate-200 shadow-sm relative overflow-hidden">
                    <div className="absolute top-0 right-0 bg-indigo-600 text-white text-xs font-bold px-3 py-1 rounded-bl-xl">
                        SERVER
                    </div>
                    <h2 className="text-lg font-semibold text-slate-500 mb-2">Cached Time</h2>
                    <div className="text-4xl font-mono font-bold text-slate-900 tracking-tight">
                        {data.formatted}
                    </div>
                    <p className="text-xs text-slate-400 mt-4">
                        This timestamp is cached on the server. Updating the page won't change it until revalidated.
                    </p>
                </div>

                {/* Control Card */}
                <div className="p-8 rounded-2xl bg-slate-50 border border-slate-200 dashed border-2 flex flex-col items-center justify-center text-center">
                    <h2 className="text-lg font-semibold text-slate-900 mb-4">Control Plane</h2>
                    <form action={revalidateTimePath}>
                        <button
                            type="submit"
                            className="px-6 py-3 bg-indigo-600 hover:bg-indigo-700 text-white font-medium rounded-xl shadow-lg shadow-indigo-200 transition-all hover:scale-105 active:scale-95"
                        >
                            Revalidate Path
                        </button>
                    </form>
                    <p className="text-sm text-slate-500 mt-4 max-w-xs">
                        Clicking this calls a Server Action to `revalidatePath`, purging the cache and fetching fresh data on the next reload.
                    </p>
                </div>
            </div>

            <TechnicalExplainer
                title="Next.js Data Cache & Server Actions"
                points={[
                    "Data Cache: Next.js extends the native fetch API to include caching. By default, fetches are cached indefinitely unless opted out.",
                    "Server Actions: Functions that run on the server, callable from Client Components. They are the standard way to handle mutations.",
                    "Revalidation: We use `revalidatePath` to purge the cache. This is smart - it only revalidates data associated with that specific path, preserving other cached segments."
                ]}
                codeSnippet={`// 1. Fetch with Cache Tags
const res = await fetch('/api/...', {
  next: { tags: ['time-data'] } 
});

// 2. Server Action
'use server'
export async function revalidate() {
  revalidatePath('/path/to/page');
}`}
            />
        </div>
    );
}

'use client';

import { useQuery } from '@tanstack/react-query';
import { useState } from 'react';
import Link from 'next/link';

const fetchRandom = async () => {
    // Artificial delay to make loading state visible
    await new Promise(r => setTimeout(r, 1000));
    return Math.random().toString(36).substring(7).toUpperCase();
};

function CachedData() {
    const { data, isLoading, isStale, isFetching, dataUpdatedAt } = useQuery({
        queryKey: ['caching-demo'],
        queryFn: fetchRandom,
        staleTime: 5000, // Considered fresh for 5 seconds
        gcTime: 10000,   // Kept in memory for 10 seconds
    });

    const timeSinceUpdate = dataUpdatedAt ? Date.now() - dataUpdatedAt : 0;

    return (
        <div className="space-y-4">
            <div className="p-6 bg-white rounded-xl border border-slate-200 shadow-sm">
                <div className="flex justify-between items-start mb-4">
                    <div>
                        <div className="text-sm font-bold text-slate-400 uppercase tracking-wider">Data ID</div>
                        {isLoading ? (
                            <div className="h-8 w-24 bg-slate-200 animate-pulse rounded mt-1" />
                        ) : (
                            <div className="text-3xl font-mono font-bold text-slate-900">{data}</div>
                        )}
                    </div>
                    <div className="text-right">
                        <div className="inline-flex items-center gap-1.5 px-2.5 py-0.5 rounded-full text-xs font-medium bg-slate-100 text-slate-600">
                            {isLoading ? 'Loading' : isFetching ? 'Background Refetch' : isStale ? 'Stale (Cached)' : 'Fresh (Cached)'}
                        </div>
                    </div>
                </div>

                <div className="text-xs text-slate-500 font-mono">
                    Last Updated: {dataUpdatedAt ? new Date(dataUpdatedAt).toLocaleTimeString() : 'Never'}
                </div>
            </div>

            <div className="text-sm text-slate-600 bg-blue-50 p-4 rounded-lg border border-blue-100">
                <strong>Experiment:</strong>
                <ul className="list-disc pl-5 space-y-1 mt-2">
                    <li>Click "Back to Features" to unmount this component.</li>
                    <li>Return within <strong>5s</strong>: Data shows instantly (Fresh).</li>
                    <li>Return between <strong>5-10s</strong>: Data shows instantly (Stale) then updates.</li>
                    <li>Return after <strong>10s</strong>: Hard loading state (Cache Garbage Collected).</li>
                </ul>
            </div>
        </div>
    );
}

export default function CachingPage() {
    const [showDemo, setShowDemo] = useState(true);

    return (
        <div className="space-y-8">
            <div>
                <h1 className="text-3xl font-bold text-slate-900">Caching Mechanics</h1>
                <p className="text-slate-600 mt-2">
                    Understanding `staleTime` and `gcTime` (Garbage Collection Time).
                </p>
            </div>

            <div className="glass-panel p-8 rounded-2xl min-h-[400px]">
                {showDemo ? (
                    <div className="space-y-8">
                        <CachedData />
                        <button
                            onClick={() => setShowDemo(false)}
                            className="w-full py-3 border-2 border-slate-200 rounded-xl text-slate-600 font-bold hover:bg-slate-50 hover:border-slate-300 transition-colors"
                        >
                            Unmount Component (Simulate Navigation Away)
                        </button>
                    </div>
                ) : (
                    <div className="flex flex-col items-center justify-center h-full py-12 text-center space-y-6">
                        <div className="text-slate-400">
                            Component Unmounted. Cache timer is ticking...
                        </div>
                        <button
                            onClick={() => setShowDemo(true)}
                            className="px-8 py-3 bg-indigo-600 text-white font-bold rounded-xl shadow-lg hover:bg-indigo-700 transition-transform active:scale-95"
                        >
                            Mount Component (Simulate Navigation Back)
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
}

'use client';

import { useQuery, useQueryClient } from '@tanstack/react-query';
import { useState } from 'react';
import TechnicalExplainer from '@/app/components/TechnicalExplainer';

const fetchSlowData = async () => {
    const res = await fetch('/api/demo/slow');
    return res.json();
};

function DataBox({ title, color }: { title: string, color: string }) {
    const { data, isLoading, isFetching } = useQuery({
        queryKey: ['dedupe-demo'],
        queryFn: fetchSlowData,
    });

    return (
        <div className={`p-6 rounded-xl border-2 ${isLoading ? 'border-dashed border-slate-300' : 'border-solid border-' + color + '-500 bg-' + color + '-50'}`}>
            <h3 className={`font-bold text-${color}-700 mb-2`}>{title}</h3>
            {isLoading ? (
                <div className="flex items-center gap-2 text-slate-500 animate-pulse">
                    <div className="w-5 h-5 border-2 border-slate-400 border-t-transparent rounded-full animate-spin" />
                    Loading...
                </div>
            ) : (
                <div className="space-y-1">
                    <div className={`text-2xl font-mono font-bold text-${color}-900`}>{data.id}</div>
                    <div className="text-xs text-slate-500">Fetched at: {data.timestamp}</div>
                    {isFetching && <div className="text-xs text-amber-600 font-semibold animate-pulse">Refetching in background...</div>}
                </div>
            )}
        </div>
    );
}

export default function DedupePage() {
    const queryClient = useQueryClient();
    const [showSecond, setShowSecond] = useState(false);

    return (
        <div className="space-y-8">
            <div>
                <h1 className="text-3xl font-bold text-slate-900">In-flight Deduplication</h1>
                <p className="text-slate-600 mt-2">
                    TanStack Query automatically shares the same promise for simultaneous requests.
                </p>
            </div>

            <div className="glass-panel p-6 rounded-2xl">
                <div className="flex gap-4 mb-6">
                    <button
                        onClick={() => queryClient.invalidateQueries({ queryKey: ['dedupe-demo'] })}
                        className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
                    >
                        Refetch All
                    </button>
                    <button
                        onClick={() => setShowSecond(!showSecond)}
                        className="px-4 py-2 border border-slate-300 rounded-lg hover:bg-slate-50 transition"
                    >
                        {showSecond ? 'Hide' : 'Show'} Component B
                    </button>
                </div>

                <div className="grid md:grid-cols-2 gap-8">
                    <DataBox title="Component A" color="indigo" />

                    {showSecond && (
                        <DataBox title="Component B" color="emerald" />
                    )}
                </div>

                <div className="mt-8 p-4 bg-amber-50 rounded-lg border border-amber-100 text-sm text-amber-800">
                    <strong>Observe:</strong> If you trigger a refetch while both are visible, they will both go into "Loading/Fetching" state, but only <strong>ONE</strong> network request will be sent.
                    They will resolve at the exact same time with the exact same Random ID.
                </div>
            </div>

            <TechnicalExplainer
                title="Request Coalescing (Deduplication)"
                points={[
                    "Shared Promises: When multiple components request the same Query Key simultaneously, TanStack Query identifies they are the same.",
                    "Optimization: It executes the promise ONCE and shares the result with all subscribers.",
                    "Efficiency: This eliminates 'waterfall' issues and redundant network calls in complex component trees."
                ]}
                codeSnippet={`// Both components call this:
useQuery({
  queryKey: ['dedupe-demo'], 
  queryFn: fetchSlowData 
});

// Result:
// 1. Component A mounts -> Starts Request
// 2. Component B mounts -> Sees existing Request -> Subscribes
// 3. Request finishes -> Both update instantly`}
            />
        </div>
    );
}

